(41, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'affirm_promo_Q1_2025', 'marketing', 'en_US', 'Save up to 50% on merchant fees with Affirm', 'Kickstart the year with buy now, pay later and get merchant rates as low as 2.9% + 30¢ per transaction for 30 days. Download the extension and complete your application from your WooCommerce dashboard by February 19, 2025 to claim the offer. Terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2025-01-23 17:35:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Vous cherchez un thème ?', 'Découvrez les thèmes compatibles avec WooCommerce et choisissez celui qui correspond le mieux à l’image de votre marque et à vos besoins.', '{}', 'unactioned', 'woocommerce-admin', '2025-01-27 16:29:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Comment personnaliser votre catalogue de produits', 'Vous souhaitez soigner l’apparence de vos images et de votre catalogue de produits tout en vous conformant à votre marque. Ce guide contient tous les conseils dont vous avez besoin pour mettre en valeur vos produits dans votre boutique.', '{}', 'unactioned', 'woocommerce-admin', '2025-01-27 16:29:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-mobile-app', 'info', 'en_US', 'Installer l’application mobile Woo', 'Installez l’application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.', '{}', 'unactioned', 'woocommerce-admin', '2025-01-27 16:29:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Commencer à accepter les paiements sur votre boutique !', 'Accepter les paiements avec le fournisseur qui vous convient, parmi plus de 100 passerelles de paiement pour WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-02-04 14:00:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'new-woo-brand-announcement-feb4-2025', 'marketing', 'en_US', 'Meet the new Woo', 'WooCommerce has a new look, but underneath, we’re still the same customizable, open-source ecommerce platform you know and love. Plus, we’ve got some major improvements to our platform and product on the way.', '{}', 'pending', 'woocommerce.com', '2025-02-06 14:27:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'paypal_bnpl_au_promo_Q1_2025', 'marketing', 'en_US', 'Australian shoppers can Pay in 4 with PayPal', 'Enable Pay in 4 messaging to let Australian customers know that they can pay over time while you get paid up front and in full. Pay in 4 is included in PayPal Payments at no additional cost. Eligible buyers can pay in four interest-free payments for purchases of $30 to $2,000 AUD. Terms apply.', '{}', 'pending', 'woocommerce.com', '2025-02-13 16:51:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-04-13 11:49:10', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'stripe_orderstatus_bug_q2_2025', 'update', 'en_US', 'Action Required: Update Order Status Manager', 'Update to the latest Order Status Manager (v1.15.5+) to ensure payments are captured. Your version of WooCommerce Order Status Manager can interfere with order processing for payment gateways (including WooCommerce Stripe Gateway v9.2.0+), which may cause orders to process without capturing payment.', '{}', 'pending', 'woocommerce.com', '2025-04-13 11:49:10', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'WooCommerce-Stripe-Gateway-Update-April-2025', 'update', 'en_US', 'Action required: Update WooCommerce Stripe Gateway to v9.4.1', '<strong>An important update for WooCommerce Stripe Payment Gateway has been released</strong>. Version 9.4.1 addresses performance issues and payment method visibility problems that occurred in version 9.4.0. Please update as soon as possible for optimal store performance. Please read <a href=\"https://developer.woocommerce.com/2025/04/18/https://developer.woocommerce.com/2025/04/18/developer-advisory-stripe-payment-gateway-9-4-1-release/\" /> this advisory</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-04-22 12:55:58', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'WooCommerce-Stripe-Gateway-Update-Info-April-2025', 'update', 'en_US', 'WooCommerce Stripe Gateway has been updated to v9.4.1', '<strong>WooCommerce Stripe Payment Gateway was recently updated</strong>. Version 9.4.1 addresses performance issues and payment method visibility problems that occurred in version 9.4.0. No further action is required. Please read <a href=\"https://developer.woocommerce.com/2025/04/18/https://developer.woocommerce.com/2025/04/18/developer-advisory-stripe-payment-gateway-9-4-1-release/\" /> this advisory post </a>for more information.', '{}', 'pending', 'woocommerce.com', '2025-04-22 12:55:58', NULL, 0, 'plain', '', 0, 0, 'info');
